import React from "react";
import { Link } from "react-router-dom";

const Home = () => {
  return (
    <div className="container text-center">
      {/* Hero Section */}
      <h1 className="mt-5" style={{ color: "#007BFF", fontWeight: "bold" }}>Welcome to AQUASMART</h1>
      <p>Your smart irrigation and water management solution.</p>
      
      {/* Navigation Buttons */}
      <div className="mt-3">
        <Link to="/register" className="btn btn-success mx-2">Get Started</Link>
        <Link to="/login" className="btn btn-primary mx-2">Login</Link>
      </div>

      {/* Features Section */}
      <h2 className="mt-5">Features</h2>
      <div className="row mt-4">
        <FeatureBox title="Real-Time Monitoring" 
                    description="Track soil moisture, temperature, and humidity with live updates." />
        <FeatureBox title="Automated Irrigation" 
                    description="Smart irrigation system that waters crops based on real-time conditions." />
        <FeatureBox title="Weather Forecast" 
                    description="Integrates weather data to optimize water usage." />
        <FeatureBox title="Crop Recommendations" 
                    description="Suggests the best crops based on soil and temperature data." />
      </div>
    </div>
  );
};

// Reusable Feature Box Component
const FeatureBox = ({ title, description }) => {
  return (
    <div className="col-md-6 mb-4">
      <div className="p-3 shadow-sm rounded bg-light">
        <h5><strong>{title}</strong></h5>
        <p>{description}</p>
      </div>
    </div>
  );
};

export default Home;
