import React from "react";
import { Link } from "react-router-dom";

const Login = () => {
  return (
    <div style={containerStyle}>
      <h1>Irrigation System Dashboard</h1>

      {/* Short project description */}
      <p style={descriptionStyle}>
        Welcome to the **Irrigation System Dashboard**. This platform allows you to monitor real-time 
        **sensor data**, check **weather forecasts**, and control your **irrigation system** remotely.
        Login to manage and optimize water usage efficiently.
      </p>

      <h2>Login</h2>
      <div style={formContainerStyle}>
        <input type="email" placeholder="Email" style={inputStyle} />
        <input type="password" placeholder="Password" style={inputStyle} />
        <button style={buttonStyle}>Login</button>
      </div>

      <p>
        Don't have an account? <Link to="/register" style={linkStyle}>Register</Link>
      </p>
    </div>
  );
};

// Styling
const containerStyle = {
  textAlign: "center",
  maxWidth: "500px",
  margin: "0 auto",
  padding: "20px",
};

const descriptionStyle = {
  fontSize: "14px",
  color: "#333",
  lineHeight: "1.5",
  marginBottom: "20px",
};

const formContainerStyle = {
  display: "flex",
  flexDirection: "column",
  gap: "10px",
  alignItems: "center",
};

const inputStyle = {
  padding: "8px",
  width: "80%",
  borderRadius: "5px",
  border: "1px solid #ccc",
};

const buttonStyle = {
  padding: "10px 20px",
  backgroundColor: "#007BFF",
  color: "white",
  border: "none",
  cursor: "pointer",
  borderRadius: "5px",
  transition: "0.3s",
};

const linkStyle = {
  color: "#007BFF",
  textDecoration: "none",
};

export default Login;
